import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import abi from './abi/abi.json';
import { getContractInfo } from './helpers/getContractInfo';
import { Form } from './components/Form';

function App() {
  const [savedText, setSavedText] = useState('');
  const [connected, setConnected] = useState(false);
  const [contractInfo, setContractInfo] = useState({});
  const { ethereum } = window;
  let contract = null;

  // UseEffect on component mount
  useEffect(() => {
    if (ethereum) {
      const address = '0x67efb6498193CaAaA13baD48c9e2DaC69dfd9b35';
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      contract = new ethers.Contract(address, abi, signer);

      // Call getContractInfo to get the contract information using async await
      getContractInfo(contract, setContractInfo);
    }
  }, []);

  const onConnectButtonClick = () => {
    if (contract && !connected) {
      ethereum.request({ method: 'eth_requestAccounts' }).then(accounts => {
        setConnected(true);
      });
    }
  };

  console.log(contractInfo);

  return (
    <div className="App">
      <h1>{contractInfo.name}</h1>

      {/* Connect Button */}
      <button onClick={onConnectButtonClick}>{connected ? 'Connected' : 'Connect wallet'}</button>

      {/* Form */}

      <Form
        contract={contract}
        connected={connected}
      />

      <button
        onClick={() => {
          if (contract && connected) {
            contract.text().then(text => {
              setSavedText(text);
            });
          }
        }}>
        Get text
      </button>

      <h3>{savedText}</h3>
    </div>
  );
}

export default App;
