import { useState } from 'react';
import { ethers } from 'ethers';
import abi from './abi/abi.json';

function App() {
  const [text, setText] = useState('');
  const [savedText, setSavedText] = useState('');
  const [connected, setConnected] = useState(false);
  const [contractInfo, setContractInfo] = useState({});
  const { ethereum } = window;
  let contract = null;

  const getContractInfo = async contract => {
    const name = await contract.name();
    const symbol = await contract.symbol();
    const decimals = await contract.decimals();
    let totalSupply = ethers.utils.formatUnits((await contract.totalSupply()).toString()).split('.')[0];
    const balanceOfAccount = (await contract.balanceOf(ethereum.selectedAddress)).toString();
    const owner = await contract.getOwner();

    setContractInfo({
      name,
      symbol,
      decimals,
      totalSupply,
      balanceOfAccount,
      owner,
    });
  };

  if (ethereum) {
    const address = '0x67efb6498193CaAaA13baD48c9e2DaC69dfd9b35';
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    contract = new ethers.Contract(address, abi, signer);
    getContractInfo(contract);
  }

  return (
    <div className="App">
      <h1>Text Contract</h1>

      <button
        onClick={() => {
          if (contract && !connected) {
            ethereum.request({ method: 'eth_requestAccounts' }).then(accounts => {
              setConnected(true);
            });
          }
        }}>
        {!connected ? 'Connect wallet' : 'Connected'}
      </button>

      <form
        onSubmit={e => {
          e.preventDefault();
          if (contract && connected) {
            contract.changeText(text).then(() => {
              setText('');
            });
          }
        }}>
        <input
          type="text"
          placeholder="Enter text"
          onChange={e => setText(e.currentTarget.value)}
          value={text}
        />
        <input
          type="submit"
          value="Change text"
        />
      </form>

      <button
        onClick={() => {
          if (contract && connected) {
            contract.text().then(text => {
              setSavedText(text);
            });
          }
        }}>
        Get text
      </button>

      <h3>{savedText}</h3>
    </div>
  );
}

export default App;
