import { useEffect, useState } from 'react';
import { ethers } from 'ethers';
import abi from './abi/abi.json';
import { getContractInfo } from './helpers/getContractInfo';
import { Form } from './components/Form';
import { ContractInformation } from './components/ContractInformation';
import { mintAmmount } from './helpers/mintAmmount';

function App() {
  const [contractObject, setContractObject] = useState({});
  const [connected, setConnected] = useState(false);
  const [contractInfo, setContractInfo] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const { ethereum } = window;
  let contract = null;

  // UseEffect on component mount
  useEffect(() => {
    if (ethereum) {
      const address = '0x67efb6498193CaAaA13baD48c9e2DaC69dfd9b35';
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      contract = new ethers.Contract(address, abi, signer);
      setContractObject(contract);

      // Call getContractInfo to get the contract information using async await
      getContractInfo(contract, setContractInfo);
    }
  }, []);

  // Connect function
  const onConnectButtonClick = () => {
    if (!connected) {
      ethereum.request({ method: 'eth_requestAccounts' }).then(accounts => {
        setConnected(true);
      });
    }
  };

  // Form submit
  const onFormSubmit = async (e, formValues) => {
    e.preventDefault();
    let transactionHash = null;
    const { mint } = formValues;

    try {
      setIsLoading(true);
      if (mint) transactionHash = await mintAmmount(mint, contractObject, ethereum.selectedAddress);
      setContractInfo(getContractInfo(contractObject, setContractInfo));
    } catch (error) {
      console.log(error);
    } finally {
      setTimeout(() => {
        setIsLoading(false);
      }, 2000);
    }
    console.log(transactionHash);
  };

  return (
    <div className="App">
      <h1>{contractInfo.name}</h1>

      {/* Connect Button */}
      <button onClick={onConnectButtonClick}>{connected ? 'Connected' : 'Connect wallet'}</button>

      {/* Information */}

      {contractInfo && connected && <ContractInformation info={contractInfo} />}

      {/* Form */}

      {connected && (
        <Form
          contract={contract}
          connected={connected}
          onSubmit={onFormSubmit}
        />
      )}
    </div>
  );
}

export default App;
