import { ethers } from 'ethers';

export const transferAmount = async (addressTo, amount, contract) => {
  console.log(addresTo);
  const resp = await contract.transfer(addressTo, ethers.utils.parseEther(amount.toString(), 'ether'));
  return resp.hash;
};
