import { useState } from 'react';

export const Form = ({ contract, connected, onSubmit }) => {
  const [formValues, setFormValues] = useState({ mint: '' });
  return (
    <form onSubmit={e => onSubmit(e, formValues)}>
      <h2>Mint</h2>
      <fieldset>
        <input
          type="text"
          placeholder="Enter ammount to mint"
          onChange={e => setFormValues({ ...formValues, mint: e.target.value })}
          value={formValues.mint}
        />
        <input
          type="submit"
          value="Mint"
        />
      </fieldset>
    </form>
  );
};
