import { ethers } from 'ethers';

export const transferAmount = async (addressTo, amount, contract) => {
  const decimalAmount = `${amount}000000000000000000`;
  console.log(contract);
  const resp = await contract.transfer(addressTo, decimalAmount);
  return resp.hash;
};
