import { BigNumber, ethers } from 'ethers';

export const transferAmount = async (addressTo, addressFrom, amount, contract) => {
  console.log(amount);
  const resp = await contract.transfer(addressFrom, addressTo, BigNumber.from(amount.toString()));
  return resp.hash;
};
