import { ethers } from 'ethers';

export const transferAmount = async (addressTo, addressFrom, amount, contract) => {
  const resp = await contract.transferFrom(addressFrom, addressTo, ethers.utils.parseUnits(amount.toString(), 'ether'));
  return resp.hash;
};
